type Channel struct {
        name string
    }
func stringInSlice(a string, list []Channel) bool {
    for _, b := range list {
        if b.name == a {
            return true
        }
    }
    return false
}

func concatSlice( list []Channel) string {
  
  var cadOriginal bytes.Buffer
  first := 1
    for _, b := range list {
      if first == 1 {
        first = 0
        cadOriginal.WriteString(b.name) 
      } else {
        cadOriginal.WriteString(","+b.name) 
      }
    }
    return cadOriginal.String()
}


